function assignRoles($user, $roles) {
    if(is_array($roles)) {
        foreach($roles as $role) {
            "INSERT INTO user_roles VALUES (null,$user,$role)";
        }
    } else if(is_integer($roles)) {
        "INSERT INTO user_roles VALUES (null,$user,$roles)";
